using System;\nusing System.IO;\n\npublic class FileComparer\n{\n    public static bool AreFilesEqual(string path1, string path2)\n    {\n        // Check if both files exist and their lengths are the same\n        FileInfo file1 = new FileInfo(path1);\n        FileInfo file2 = new FileInfo(path2);\n\n        if (!file1.Exists || !file2.Exists || (file1.Length != file2.Length))\n            return false;\n\n        const int bufferSize = 4096; // Use a buffer size of 4K\n        byte[] buffer1 = new byte[bufferSize];\n        byte[] buffer2 = new byte[bufferSize];\n\n        using (FileStream fs1 = file1.OpenRead(), fs2 = file2.OpenRead())\n        {\n            while (true)\n            {\n                int bytesRead1 = fs1.Read(buffer1, 0, bufferSize);\n                int bytesRead2 = fs2.Read(buffer2, 0, bufferSize);\n\n                if (bytesRead1 != bytesRead2 || !BuffersAreEqual(buffer1, buffer2, bytesRead1))\n                    return false;\n\n                if (bytesRead1 == 0) // End of both files\n                    return true;\n            }\n        }\n    }\n\n    private static bool BuffersAreEqual(byte[] buffer1, byte[] buffer2, int length)\n    {\n        for (int i = 0; i < length; i++)\n        {\n            if (buffer1[i] != buffer2[i])\n                return false;\n        }\n        return true;\n    }\n}