using System;\nusing System.Media;\nusing System.Threading.Tasks;\nusing System.Windows.Forms;\n\npublic partial class Form1 : Form {\n    private readonly string[] sounds = { "sound1.wav", "sound2.wav", "sound3.wav" };\n    private readonly Image[] images = { Image.FromFile("image1.png"), Image.FromFile("image2.png"), Image.FromFile("image3.png") };\n\n    public Form1() {\n        InitializeComponent();\n        PlaySoundsAndUpdateImagesAsync().ConfigureAwait(false); // Start the async sound playing and image changing\n    }\n\n    private async Task PlaySoundsAndUpdateImagesAsync() {\n        for (int i = 0; i < sounds.Length; i++) {\n            await Task.Run(() => {\n                using (SoundPlayer player = new SoundPlayer(sounds[i])) {\n                    player.PlaySync(); // Plays the sound synchronously in a background task\n                }\n            });\n\n            pictureBox1.Image = images[i]; // Update the image after each sound completion\n        }\n    }\n}