using Microsoft.CodeAnalysis;\nusing Microsoft.CodeAnalysis.CSharp;\nusing Microsoft.CodeAnalysis.MSBuild;\nusing System;\nusing System.Linq;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    public static async Task Main(string[] args)\n    {\n        // Path to your solution file\n        string solutionPath = @"path\to\your\solution.sln";\n\n        // Initialize the MSBuildWorkspace\n        using (var workspace = MSBuildWorkspace.Create())\n        {\n            var solution = await workspace.OpenSolutionAsync(solutionPath);\n\n            foreach (var project in solution.Projects)\n            {\n                Console.WriteLine($"Checking project: {project.Name}");\n\n                foreach (var document in project.Documents)\n                {\n                    var root = await document.GetSyntaxRootAsync();\n                    var variableUsages = root\n                        .DescendantNodes()\n                        .Where(n => n.IsKind(Microsoft.CodeAnalysis.CSharp.SyntaxKind.IdentifierName))\n                        .Select(id => id as Microsoft.CodeAnalysis.CSharp.Syntax.IdentifierNameSyntax)\n                        .Where(id => id != null && id.Identifier.Text == "YourVariableName")\n                        .Count();\n\n                    if (variableUsages > 0)\n                    {\n                        Console.WriteLine($"Variable 'YourVariableName' used in document: {document.Name} ({variableUsages} times)");\n                    }\n                }\n            }\n        }\n    }\n}