using System;\nusing System.Windows.Forms;\nusing WebKit;\n\nnamespace WebKitDotNetExample\n{\n    public partial class Form1 : Form\n    {\n        private WebView _webView;\n\n        public Form1()\n        {\n            InitializeComponent();\n            InitializeWebKitBrowser();\n        }\n\n        private void InitializeWebKitBrowser()\n        {\n            // Create a new instance of the WebView control\n            _webView = new WebView();\n\n            // Set DockStyle to fill so it uses all available space in the Form\n            _webView.Dock = DockStyle.Fill;\n\n            // Load a URL\n            _webView.Navigate("https://www.example.com");\n\n            // Add the WebView to the form's controls collection\n            this.Controls.Add(_webView);\n        }\n\n        protected override void OnLoad(EventArgs e)\n        {\n            base.OnLoad(e);\n\n            // Start the web view\n            if (WebKitBrowser.IsInitialized)\n                _webView.Start();\n        }\n    }\n}