using System;\nusing Microsoft.Web.Administration;\n\npublic class IisHostHeaderManager\n{\n    public void AddHostHeader(string siteName, string ipAddress, int port, string hostHeader)\n    {\n        using (ServerManager serverManager = new ServerManager())\n        {\n            Site site = serverManager.Sites[siteName];\n            if (site != null)\n            {\n                BindingInformation bindingInfo = $"{ipAddress}:{port}:{hostHeader}";\n                Binding binding = site.Bindings.Add(bindingInfo);\n                serverManager.CommitChanges();\n                Console.WriteLine($"Host header '{hostHeader}' added successfully to site '{siteName}'.");\n            }\n            else\n            {\n                Console.WriteLine($"Site '{siteName}' not found.");\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        IisHostHeaderManager manager = new IisHostHeaderManager();\n        // Example usage\n        manager.AddHostHeader("Default Web Site", "127.0.0.1", 80, "admin.example.com");\n    }\n}