// Example of setting HttpOnly and Secure flags in ASP.NET FormsAuthentication cookie\nFormsAuthenticationTicket authTicket = new FormsAuthenticationTicket(\n    1,\n    username,\n    DateTime.Now,\n    DateTime.Now.AddMinutes(30), // Set timeout as needed\n    isPersistent,               // If true, the ticket will be persistent across browser sessions\n    userData,\n    FormsAuthentication.FormsCookiePath);\n\nstring encryptedTicket = FormsAuthentication.Encrypt(authTicket);\nHttpCookie authCookie = new HttpCookie(FormsAuthentication.FormsCookieName, encryptedTicket)\n{\n    Path = FormsAuthentication.FormsCookiePath,\n    HttpOnly = true,\n    Secure = true // Ensure cookie is sent only over HTTPS\n};\nResponse.Cookies.Add(authCookie);