using (var dataContext = new YourDataContext())\n{\n    // Assuming 'personRequest' is the updated Person object received from the service layer\n    var personToUpdate = dataContext.Person.Find(personRequest.PersonID);\n\n    if (personToUpdate != null)\n    {\n        dataContext.Entry(personToUpdate).CurrentValues.SetValues(personRequest);\n        dataContext.SaveChanges();\n    }\n    else\n    {\n        // Handle case when the entity is not found\n        throw new ArgumentException("Person ID not found.");\n    }\n}