using System.IO;\nusing iTextSharp.text.pdf;\n\npublic void SplitPdf(string sourcePath, string destinationFolder)\n{\n    PdfReader reader = new PdfReader(sourcePath);\n    for (int page = 1; page <= reader.NumberOfPages; page++)\n    {\n        string outputPath = Path.Combine(destinationFolder, $"page_{page}.pdf");\n        using (FileStream fs = new FileStream(outputPath, FileMode.Create, FileAccess.Write, FileShare.None))\n        {\n            Document outputDocument = new Document();\n            PdfCopy pdfCopy = new PdfCopy(outputDocument, fs);\n            outputDocument.Open();\n\n            PdfImportedPage pageToPrint = pdfCopy.GetImportedPage(reader, page);\n            pdfCopy.AddPage(pageToPrint);\n            outputDocument.Close();\n        }\n    }\n    reader.Close();\n}