public void AddComment(int postId)\n{\n    // logic to insert comment into Comments table...\n    \n    using (var connection = new SqlConnection(connectionString))\n    {\n        var query = "UPDATE Posts SET CommentCount = CommentCount + 1 WHERE PostID = @PostId";\n        using (var command = new SqlCommand(query, connection))\n        {\n            command.Parameters.AddWithValue("@PostId", postId);\n            connection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n}\n\npublic void DeleteComment(int commentId, int postId)\n{\n    // logic to delete comment from Comments table...\n    \n    using (var connection = new SqlConnection(connectionString))\n    {\n        var query = "UPDATE Posts SET CommentCount = CommentCount - 1 WHERE PostID = @PostId";\n        using (var command = new SqlCommand(query, connection))\n        {\n            command.Parameters.AddWithValue("@PostId", postId);\n            connection.Open();\n            command.ExecuteNonQuery();\n        }\n    }\n}