var result = from s in db.Sales\n             join u in db.Users on s.UserID equals u.ID\n             where s.ID == 1\n             select new\n             {\n                 Sale = s,\n                 UserName = u.Name // This ensures the user data is loaded with the sale.\n             };\n\nvar saleInfo = result.FirstOrDefault();\nif (saleInfo != null)\n{\n    string username = saleInfo.UserName;\n}\nelse\n{\n    throw new InvalidOperationException("Sale not found");\n}