using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string driveLetter = "C:"; // Specify the drive letter you want to check\n\n        DriveInfo driveInfo = new DriveInfo(driveLetter);\n\n        if (driveInfo.IsReady && driveInfo.DriveType == DriveType.Fixed)\n        {\n            Console.WriteLine($"{driveInfo.Name} is a Hard Drive.");\n        }\n        else if (driveInfo.IsReady && driveInfo.DriveType == DriveType.Network)\n        {\n            Console.WriteLine($"{driveInfo.Name} is a Network Drive.");\n        }\n        else if (driveInfo.DriveType == DriveType.CDRom)\n        {\n            Console.WriteLine($"{driveInfo.Name} is a CD-ROM.");\n        }\n        else if (driveInfo.DriveType == DriveType.Removable)\n        {\n            Console.WriteLine($"{driveInfo.Name} is a Removable Drive (likely a floppy or USB).");\n        }\n    }\n}