// Pseudo-code example for accessing and updating cached data safely\npublic void AllocateNewItem()\n{\n    string lowestWorkloadUserId;\n\n    using (var transactionScope = new TransactionScope())\n    {\n        // Get the user with low workload from DB with UPDLOCK\n        lowestWorkloadUserId = GetUserWithLowestWorkload();\n        \n        // Update the database immediately within the same transaction scope\n        UpdateUserWorkload(lowestWorkloadUserId, 1);\n\n        // Commit to ensure all changes are saved together\n        transactionScope.Complete();\n    }\n\n    // Optionally update cache after successful DB update\n    RefreshCache(lowestWorkloadUserId);\n}