// Create an instance of the dataset\nDataSet dataSet = new DataSet();\n\n// Assume we have filled the dataset with data\n\n// Create and initialize the DataGrid view.\nDataGridView dataGridView1 = new DataGridView();\ndataGridView1.Dock = DockStyle.Fill;\n\n// Set AutoGenerateColumns to false. This means that no automatic columns will be created when the DataSource is set.\ndataGridView1.AutoGenerateColumns = false;\nforeach (DataColumn col in dataSet.Tables[0].Columns)\n{\n    // Create a new DataGridViewTextBoxColumn\n    DataGridViewTextBoxColumn column = new DataGridViewTextBoxColumn();\n    \n    // Make the column read only and name it same as Data Field\n    column.DataPropertyName = col.ColumnName;\n    column.HeaderText = col.Caption;\n    column.Name = col.ColumnName;\n\n    dataGridView1.Columns.Add(column);\n}\n\n// Set the form datasource and add datagrid to form's controls\ndataGridView1.DataSource = dataSet.Tables[0];\nthis.Controls.Add(dataGridView1);