public class TranslationController : Controller\n{\n    private readonly ITranslationService _translationService;\n\n    public TranslationController(ITranslationService translationService)\n    {\n        _translationService = translationService;\n    }\n\n    // Traditional MVC view action\n    public ActionResult Index()\n    {\n        var model = _translationService.GetAllTranslations();\n        return View(model);\n    }\n\n    // RESTful API endpoint to get translations as JSON\n    [HttpGet]\n    public JsonResult GetTranslations()\n    {\n        var translations = _translationService.GetAllTranslations();\n        return Json(translations, JsonRequestBehavior.AllowGet);\n    }\n\n    // RESTful API endpoint to update a translation\n    [HttpPut]\n    public JsonResult UpdateTranslation(int id, string newTranslation)\n    {\n        _translationService.UpdateTranslation(id, newTranslation);\n        return Json(new { success = true });\n    }\n}