using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private ContextMenuStrip contextMenu;\n\n    public MainForm()\n    {\n        // Set up the main form without a title bar\n        this.FormBorderStyle = FormBorderStyle.None;\n        this.WindowState = FormWindowState.Maximized;\n\n        // Create and attach the context menu\n        contextMenu = new ContextMenuStrip();\n        ToolStripMenuItem minimizeItem = new ToolStripMenuItem("Minimize");\n        minimizeItem.Click += (sender, e) => MinimizeForm();\n        contextMenu.Items.Add(minimizeItem);\n        this.ContextMenuStrip = contextMenu;\n    }\n\n    protected override void OnKeyDown(KeyEventArgs e)\n    {\n        base.OnKeyDown(e);\n\n        // Check for the specific key combination like Ctrl + M to minimize\n        if (e.Control && e.KeyCode == Keys.M)\n        {\n            MinimizeForm();\n        }\n    }\n\n    private void MinimizeForm()\n    {\n        this.WindowState = FormWindowState.Minimized;\n    }\n}\n\nstatic class Program\n{\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MainForm());\n    }\n}