// Assuming 'dt' is your original DataTable containing all columns\nDataTable dt = dataset.Tables["A"];\n\n// Create a new DataTable to hold only the columns you need\nDataTable filteredDT = new DataTable();\n\n// Add only the desired columns (a3 and a4) to the new DataTable\nfilteredDT.Columns.Add(dt.Columns["a3"]);\nfilteredDT.Columns.Add(dt.Columns["a4"]);\n\n// Copy rows from the original DataTable to your new DataTable,\n// but insert only the data for the columns we added\nforeach (DataRow row in dt.Rows)\n{\n    var newRow = filteredDT.NewRow();\n    newRow["a3"] = row["a3"];\n    newRow["a4"] = row["a4"];\n    filteredDT.Rows.Add(newRow);\n}\n\n// Now bind 'filteredDT' to your DataGridView or any other UI component\ndataGridView.DataSource = filteredDT;