using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    [DllImport("user32.dll")]\n    private static extern bool PostMessage(IntPtr hWnd, uint Msg, IntPtr wParam, IntPtr lParam);\n\n    // Key event codes\n    const uint WM_KEYDOWN = 0x0100;\n    const uint WM_KEYUP = 0x0101;\n\n    // Virtual key code for F4\n    const int VK_F4 = 0x73;\n\n    static void Main(string[] args)\n    {\n        // Replace "YourProcessWindowTitle" with the actual title of your process's window\n        IntPtr hWnd = FindWindow(null, "YourProcessWindowTitle");\n\n        if (hWnd == IntPtr.Zero)\n        {\n            Console.WriteLine("Failed to find the window.");\n            return;\n        }\n\n        // Simulate F4 key press\n        PostMessage(hWnd, WM_KEYDOWN, (IntPtr)VK_F4, (IntPtr)0);\n        PostMessage(hWnd, WM_KEYUP, (IntPtr)VK_F4, (IntPtr)0);\n\n        Console.WriteLine("F4 key sent.");\n    }\n}