using System;\nusing System.ComponentModel.DataAnnotations;\n\npublic class LoginViewModel\n{\n    [Required(ErrorMessage = "Username is required.")]\n    public string Username { get; set; }\n\n    [Required(ErrorMessage = "Password is required.")]\n    public string Password { get; set; }\n\n    private bool _isValidLogin;\n    public bool IsValidLogin\n    {\n        get => _isValidLogin;\n        set\n        {\n            if (_isValidLogin != value)\n            {\n                _isValidLogin = value;\n                OnPropertyChanged(nameof(IsValidLogin));\n            }\n        }\n    }\n\n    // Method to validate the credentials\n    public void Validate()\n    {\n        var validationContext = new ValidationContext(this);\n        var results = new List<ValidationResult>();\n        \n        // Check all DataAnnotations attributes\n        IsValidLogin = Validator.TryValidateObject(this, validationContext, results);\n\n        // Add any custom validation logic here\n        if (!string.IsNullOrEmpty(Username) && Username.Length < 5)\n        {\n            IsValidLogin = false;\n            results.Add(new ValidationResult("Username must be at least 5 characters long.", new[] { nameof(Username) }));\n        }\n    }\n\n    private void OnPropertyChanged(string propertyName)\n    {\n        // Trigger property changed notification\n    }\n}