private static Random randomNumber = new Random(); // Create a single instance of Random outside the method\n\nprivate static string RandomString(int Length)\n{\n    StringBuilder sb = new StringBuilder();\n\n    for (int i = 0; i < Length; ++i) // Changed to < Length to avoid one extra character\n    {\n        int x = randomNumber.Next(65, 123); // Changed to 123 to include lowercase letters up to 'z'\n        sb.Append(Convert.ToChar(x));\n    }\n    return sb.ToString();        \n}