using System.IO;\nusing System.Security.AccessControl;\n\nstring filePath = @"C:\path\to\your\file.txt";\nFileSecurity fileSecurity = File.GetAccessControl(filePath);\n\nFileSystemRights readWritePermissions = FileSystemRights.Read | FileSystemRights.Write;\nstring account = "DOMAIN\\user";\n\n// Remove all access rules for the specified account.\nAuthorizationRuleCollection aclCollection = fileSecurity.GetAccessRules(true, true, typeof(System.Security.Principal.NTAccount));\nforeach (FileSystemAccessRule rule in aclCollection)\n{\n    if ((string)rule.IdentityReference == account && ((int)rule.FileSystemRights & (int)readWritePermissions) != 0)\n        fileSecurity.RemoveAccessRule(new FileSystemAccessRule(rule));\n\n}\n\n// Add the new access rights.\nfileSecurity.AddAccessRule(new FileSystemAccessRule(account, readWritePermissions, AccessControlType.Allow));\n\nFile.SetAccessControl(filePath, fileSecurity);