public void ProcessRequest(HttpContext context)\n{\n    // Check if the request is secure (HTTPS)\n    if (!context.Request.IsSecureConnection)\n    {\n        // If not secure, return a 403 Forbidden response or redirect to HTTPS\n        context.Response.StatusCode = 403;\n        context.Response.StatusDescription = "SSL Required";\n        context.Response.End();\n        \n        // Alternatively, you can redirect to the same page with HTTPS:\n        // string url = "https://" + context.Request.Url.Host + context.Request.RawUrl;\n        // context.Response.Redirect(url);\n        return;\n    }\n\n    // Proceed with your handler logic\n    context.Response.Write("This is a secure request.");\n}