using System;\nusing Microsoft.Office.Interop.Access;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Application accessApp = new Application();\n        string dbPath = @"C:\path\to\your\db.accdb"; // Path to your Access database file\n        string oldTableName = "tableName";\n        string newTableName = "newTableName";\n\n        try\n        {\n            // Open the database\n            accessApp.OpenCurrentDatabase(dbPath, false);\n\n            // Iterate through all queries in the database\n            foreach (DAO.QueryDef query in accessApp.CurrentDb().QueryDefs)\n            {\n                if (!string.IsNullOrEmpty(query.SQL) && query.SQL.Contains(oldTableName))\n                {\n                    Console.WriteLine($"Updating query: {query.Name}");\n                    string updatedSQL = query.SQL.Replace(oldTableName, newTableName);\n                    query.SQL = updatedSQL;\n                }\n            }\n\n            Console.WriteLine("All queries have been updated.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n        finally\n        {\n            // Close the application\n            accessApp.CloseCurrentDatabase();\n            accessApp.Quit();\n            System.Runtime.InteropServices.Marshal.ReleaseComObject(accessApp);\n        }\n    }\n}