public class MyWebPart : Microsoft.SharePoint.WebPartPages.WebPart\n{\n    protected override void OnInit(EventArgs e)\n    {\n        base.OnInit(e);\n\n        // Create an instance of the fully trusted wrapper and call it\n        var unmanagedCodeWrapper = new UnmanagedCodeWrapper();\n        try\n        {\n            unmanagedCodeWrapper.CallUnmanagedMethod();\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions that may occur during interaction with unmanaged code\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}