public interface ISubtractable<T>\n{\n    T Subtract(T other);\n}\n\npublic class Number : ISubtractable<Number>\n{\n    public int Value { get; }\n\n    public Number(int value)\n    {\n        Value = value;\n    }\n\n    public Number Subtract(Number other)\n    {\n        return new Number(this.Value - other.Value);\n    }\n}\n\npublic static class Calculator\n{\n    public static T Subtract<T>(T a, T b) where T : ISubtractable<T>\n    {\n        return a.Subtract(b);\n    }\n}\n\n// Example usage:\nNumber num1 = new Number(10);\nNumber num2 = new Number(5);\n\nvar result = Calculator.Subtract(num1, num2); // This will subtract 5 from 10