protected void Page_Load(object sender, EventArgs e)\n{\n    string currentPage = Path.GetFileNameWithoutExtension(Request.Url.AbsolutePath);\n\n    switch (currentPage)\n    {\n        case "screenshots":\n            screenshots.Attributes.CssStyle["class"] += " active";\n            break;\n        case "future":\n            future.Attributes.CssStyle["class"] += " active";\n            break;\n        case "news":\n            news.Attributes.CssStyle["class"] += " active";\n            break;\n        case "download":\n            download.Attributes.CssStyle["class"] += " active";\n            break;\n        default: // Default to home\n            home.Attributes.CssStyle["class"] += " active";\n            break;\n    }\n}