using System;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=yourUsername;Password=yourPassword;Data Source=YourDataSource;";\n        \n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                Console.WriteLine("Connected to Oracle database successfully!");\n\n                // Example command and execution\n                using (OracleCommand cmd = connection.CreateCommand())\n                {\n                    cmd.CommandText = "SELECT 1 FROM dual"; // Oracle equivalent\n                    object result = cmd.ExecuteScalar();\n                    Console.WriteLine($"Result of query: {result}");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error connecting to Oracle database: " + ex.Message);\n            }\n        }\n    }\n}