public class Order\n{\n    public int Version { get; set; }\n    public DateTime OrderDate { get; set; } // v2 added field\n\n    // Other fields...\n}\n\npublic void SyncOrders(Order mobileOrder, Order mainAppOrder)\n{\n    if (mobileOrder.Version == 1)\n    {\n        // For version 1 of mobile app\n        SyncCommonFields(mobileOrder, mainAppOrder);\n    }\n    else if (mobileOrder.Version == 2)\n    {\n        // For version 2 of mobile app\n        SyncCommonFields(mobileOrder, mainAppOrder);\n        SyncV2SpecificFields(mobileOrder, mainAppOrder);\n    }\n}\n\nprivate void SyncCommonFields(Order mobileOrder, Order mainAppOrder)\n{\n    // Sync fields common to all versions\n    // Example: mainAppOrder.CustomerId = mobileOrder.CustomerId;\n}\n\nprivate void SyncV2SpecificFields(Order mobileOrder, Order mainAppOrder)\n{\n    if (mobileOrder.OrderDate != DateTime.MinValue) // or some default value\n    {\n        mainAppOrder.OrderDate = mobileOrder.OrderDate;\n    }\n}