[AcceptVerbs(HttpVerbs.Post)]\npublic ActionResult LogIn(MyProject.Website.ViewModels.Shared.LogIn model)\n{\n    // Explicitly call TryValidateModel to perform validation on the model\n    if (TryValidateModel(model))\n    {\n        // If the model is valid, proceed with your logic\n        return View("Success", model);\n    }\n\n    // If the model state is not valid, re-render the view so that \n    // error messages can be shown.\n    return View(model);\n}