// Assuming you have access to the DataSet and relation is already defined.\npublic class OrderWithEmployeeName\n{\n    public DataRowView DataRow { get; }\n\n    public OrderWithEmployeeName(DataRowView row)\n    {\n        DataRow = row;\n    }\n\n    public string Something => DataRow["Something"] as string;\n    public string Anotherthing => DataRow["Anotherthing"] as string;\n\n    public string EmpName\n    {\n        get\n        {\n            var employeeID = (int)DataRow["EmpID"];\n            var parentTable = DataRow.DataView.Table;\n            var relation = parentTable.DataSet.Relations.Cast<DataRelation>()\n                                    .FirstOrDefault(r => r.ParentTable == parentTable);\n\n            if (relation != null && relation.ParentKey.Length > 0)\n            {\n                var employeeRow = DataRow.GetParentRow(relation);\n                return employeeRow?[relation.ParentColumns[0].ColumnName] as string;\n            }\n            return null;\n        }\n    }\n}