public bool HasValidAttributes(string FileName)\n{\n    // Ensure TempPath is not null or empty\n    if (string.IsNullOrEmpty(TempPath))\n    {\n        throw new InvalidOperationException("TempPath has not been initialized.");\n    }\n\n    // Ensure FileName is not null\n    if (FileName == null)\n    {\n        throw new ArgumentNullException(nameof(FileName), "FileName cannot be null.");\n    }\n\n    string FullPath = TempPath + FileName;\n    \n    try\n    {\n        System.Drawing.Image Adimg = System.Drawing.Image.FromFile(MapPath(FullPath));\n        // Proceed with other operations if needed\n        return true; // or your relevant logic\n    }\n    catch (System.IO.FileNotFoundException ex)\n    {\n        // Handle the case where the file is not found\n        Console.WriteLine($"File not found: {FullPath}");\n        return false;\n    }\n    catch (Exception ex)\n    {\n        // Catch and handle other potential exceptions\n        Console.WriteLine($"An error occurred: {ex.Message}");\n        return false;\n    }\n}