using System;\nusing System.Diagnostics;\nusing System.Windows.Forms;\n\npublic class HostApp : Form {\n    private Button openTabButton;\n\n    public HostApp() {\n        openTabButton = new Button();\n        openTabButton.Text = "Open New Tab";\n        openTabButton.Click += OpenTabButton_Click;\n        this.Controls.Add(openTabButton);\n    }\n\n    private void OpenTabButton_Click(object sender, EventArgs e) {\n        ProcessStartInfo startInfo = new ProcessStartInfo("path_to_your_tab_process_app.exe");\n        Process.Start(startInfo); // This will launch a new process for each tab\n    }\n\n    [STAThread]\n    public static void Main() {\n        Application.Run(new HostApp());\n    }\n}