using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string storedProcedureName = "YourStoredProcedure";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                SqlCommand command = new SqlCommand($"SET FMTONLY ON; EXEC {storedProcedureName}; SET FMTONLY OFF;", connection);\n                SqlDataReader reader = command.ExecuteReader();\n\n                // If you get here without exceptions, the stored procedure compiled successfully.\n                Console.WriteLine($"{storedProcedureName} compiled successfully.");\n            }\n            catch (SqlException ex)\n            {\n                // Handle compilation errors\n                Console.WriteLine($"Compilation error in {storedProcedureName}: {ex.Message}");\n            }\n        }\n    }\n}