using System.Data;\n\npublic void ProcessData()\n{\n    DataSet myDataSet = new DataSet();\n    // Assume we have populated the DataSet\n\n    try\n    {\n        // Process data here\n    }\n    finally\n    {\n        // Clear each DataTable\n        foreach (DataTable table in myDataSet.Tables)\n        {\n            table.Clear();\n        }\n\n        // Clear the DataSet\n        myDataSet.Clear();\n\n        // Optionally, call Dispose if required by your coding standards or future-proofing\n        myDataSet.Dispose();\n    }\n}