// Server-side code (ASP.NET MVC Controller)\nusing Microsoft.AspNetCore.Mvc;\nusing Microsoft.AspNetCore.Http;\n\npublic class HomeController : Controller\n{\n    [HttpPost]\n    public IActionResult SetTabId([FromBody] dynamic data)\n    {\n        string tabId = data.tabId;\n        HttpContext.Session.SetString(tabId, "SomeData");\n\n        // Store and retrieve other session data specific to the tab ID\n\n        return Ok();\n    }\n\n    [HttpGet]\n    public IActionResult GetSessionForTab(string tabId)\n    {\n        string sessionData = HttpContext.Session.GetString(tabId);\n        \n        // Use this data as needed\n\n        return Json(sessionData);\n    }\n}