// XAML part to include WebBrowser control\n<UserControl x:Class="SilverlightApp.MainPage"\n    xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation" \n    xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml" \n    xmlns:d="http://schemas.microsoft.com/expression/blend/2008" \n    xmlns:mc="http://schemas.openxmlformats.org/markup-compatibility/2006"\n    mc:Ignorable="d"\n    d:DesignHeight="480" d:DesignWidth="640">\n    <Grid x:Name="LayoutRoot">\n        <WebBrowser x:Name="webBrowser" Width="600" Height="400"/>\n    </Grid>\n</UserControl>\n\n// Code-behind to load a web page\nusing System.Windows.Controls;\n\nnamespace SilverlightApp\n{\n    public partial class MainPage : UserControl\n    {\n        public MainPage()\n        {\n            InitializeComponent();\n            webBrowser.Navigate(new Uri("http://www.example.com", UriKind.Absolute));\n        }\n    }\n}