using System;\nusing System.ComponentModel.Composition;\n\n// Define interfaces for MVPTriad.\npublic interface IView { }\npublic interface IPresenter { }\npublic interface IModel { }\n\n// Export implementations.\n[Export(typeof(IModel))]\npublic class MyModel : IModel { }\n\n[Export]\npublic class MyPresenter : IPresenter\n{\n    public MyPresenter(IModel model, IView view)\n    {\n        // Wire up Model and View here.\n    }\n}\n\n[Export(typeof(IView))]\npublic class MyView : UserControl, IView \n{\n    [ImportingConstructor]\n    public MyView(ExportFactory<IPresenter> presenterFactory)\n    {\n        var presenter = presenterFactory.CreateExport().Value;\n        // Use the created presenter\n    }\n}\n\n// Shell imports the View.\n[Export]\npublic class MyShellViewModel\n{\n    private readonly IView _view;\n\n    [ImportingConstructor]\n    public MyShellViewModel(IView view)\n    {\n        _view = view;\n    }\n\n    public void ShowView()\n    {\n        // Logic to display this view.\n    }\n}