public class RsaKeyRetrieval\n{\n    public RSAParameters RetrievePrivateKey(string filePath)\n    {\n        using (TextReader reader = File.OpenText(filePath))\n        {\n            var pemReader = new PemReader(reader);\n            AsymmetricCipherKeyPair keyPair = (AsymmetricCipherKeyPair)pemReader.ReadObject();\n\n            RsaPrivateCrtKeyParameters privateKeyParams = (RsaPrivateCrtKeyParameters)keyPair.Private;\n            RSAParameters rsaParams = DotNetUtilities.ToRSAParameters(privateKeyParams);\n\n            return rsaParams;\n        }\n    }\n\n    public RSAParameters RetrievePublicKey(string filePath)\n    {\n        using (TextReader reader = File.OpenText(filePath))\n        {\n            var pemReader = new PemReader(reader);\n            RsaKeyParameters publicKeyParams = (RsaKeyParameters)pemReader.ReadObject();\n\n            RSAParameters rsaParams = DotNetUtilities.ToRSAParameters(publicKeyParams);\n\n            return rsaParams;\n        }\n    }\n}