// Using RazorLight example\nusing RazorLight;\n\nstring userName = "John Doe";\nvar template = @"\n<h1>Heading Here</h1>\n<p>Dear @Model.Name,</p>\n<br />\n<p>First part of the email body goes here</p>";\n\nEngineOptions options = new EngineOptions\n{\n    Caching = false,\n};\n\nvar engine = EngineFactory.Create(new RazorLightDiskCache("compiled_views"), options);\nstring result = engine.CompileRenderAsync("templateKey", template, new { Name = userName }).Result;\n\n// Rest of your code to send the email using System.Net.Mail\nusing (var client = new SmtpClient())\n{\n    var mailMessage = new MailMessage("from@example.com", "to@example.com", "Subject", result);\n    mailMessage.IsBodyHtml = true;\n    client.Send(mailMessage);\n}