protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initialize the connection string and query\n        string connectionString = "your_connection_string_here";\n        string query = "SELECT ColumnName FROM YourTable";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n            try\n            {\n                connection.Open();\n                SqlDataReader reader = command.ExecuteReader();\n\n                StringBuilder options = new StringBuilder();\n                while (reader.Read())\n                {\n                    // Assuming the column you want to display is named "ColumnName"\n                    string itemValue = reader["ColumnName"].ToString();\n                    options.AppendFormat("<option value=\"{0}\">{0}</option>", Server.HtmlEncode(itemValue));\n                }\n                \n                // Inject the generated options into your HTML <select>\n                listboxPlaceholder.InnerHtml = options.ToString();\n            }\n            catch (Exception ex)\n            {\n                // Handle exceptions\n            }\n        }\n    }\n}