using System;\nusing System.Drawing;\nusing System.Windows.Forms;\nusing ZedGraph;\n\npublic class ChartExample : Form\n{\n    private ZedGraphControl zedGraphControl1;\n\n    public ChartExample()\n    {\n        // Initialize ZedGraphControl\n        zedGraphControl1 = new ZedGraphControl();\n        this.Controls.Add(zedGraphControl1);\n        zedGraphControl1.Dock = DockStyle.Fill;\n        \n        CreateChart(zedGraphControl1.GraphPane);  \n        zedGraphControl1.Invalidate();  // Force the control to redraw\n    }\n\n    private void CreateChart(GraphPane myPane)\n    {\n        // Set the title and axis labels\n        myPane.Title.Text = "My Test Chart";\n        myPane.XAxis.Title.Text = "X Axis";\n        myPane.YAxis.Title.Text = "Y Axis";\n\n        // Make up some data points based on the Sine function\n        PointPairList list1 = new PointPairList();\n        for (double x = 0; x <= Math.PI * 2; x += 0.1)\n            list1.Add(x, Math.Sin(x));\n\n        LineItem myCurve1 = myPane.AddCurve("Sine Wave",\n                                            list1,\n                                            Color.Blue,\n                                            SymbolType.Circle);\n\n        // Tell ZedGraph to re-calculate the axis ranges\n        myPane.AxisChange();\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.Run(new ChartExample());\n    }\n}