using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\n// Setup Chrome options for headless mode\nvar chromeOptions = new ChromeOptions();\nchromeOptions.AddArgument("--headless");\nchromeOptions.AddArgument("--disable-gpu"); // Disable GPU acceleration (may be needed)\n\n// Initialize the ChromeDriver\nIWebDriver driver = new ChromeDriver(chromeOptions);\n\ntry {\n    // Load your HTML content into a local file or via a URL\n    string filePath = @"C:\path\to\your\file.html"; // Replace with your file path or URL\n    driver.Navigate().GoToUrl(new System.Uri(filePath));\n\n    // Take a screenshot and save it to a file\n    var screenshot = ((ITakesScreenshot)driver).GetScreenshot();\n    string imageFilePath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.Desktop), "screenshot.png");\n    screenshot.SaveAsFile(imageFilePath);\n}\nfinally {\n    driver.Quit(); // Quit the driver\n}