// Example of using AntiForgeryToken attribute in ASP.NET MVC to prevent CSRF\n[HttpPost]\n[ValidateAntiForgeryToken]\npublic ActionResult SensitiveOperation(OperationModel model)\n{\n    if (!ModelState.IsValid) return View(model);\n    \n    // Check user authorization again for sensitive operations\n    if(!UserHasPermission(User.Identity.Name, "SensitiveAction"))\n        throw new UnauthorizedAccessException("You are not authorized to perform this action.");\n\n    // Business logic here\n    \n    return RedirectToAction("Success");\n}