// Ensure you have added a reference to OpenNETCF.WindowsCE.PocketOutlook.dll\nusing OpenNETCF.WindowsCE;\nusing OpenNETCF.WindowsCE.Notification;\n\npublic void DeleteAllSMS()\n{\n    try\n    {\n        // Create an instance of the MessagingApplication object.\n        MessagingApplication messaging = new MessagingApplication();\n\n        // Retrieve the SMS messages.\n        OutlookMessageCollection receivedMessages = messaging.GetMessages(MessageType.SmsReceived);\n        OutlookMessageCollection sentMessages = messaging.GetMessages(MessageType.SmsSent);\n\n        // Delete each message.\n        foreach (OutlookMessage message in receivedMessages)\n        {\n            message.Delete();\n        }\n\n        foreach (OutlookMessage message in sentMessages)\n        {\n            message.Delete();\n        }\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Error deleting SMS messages: " + ex.Message);\n    }\n}