using System;\nusing System.Collections.Generic;\n\npublic class Configuration\n{\n    public string Name { get; set; }\n    public string Value { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        List<Configuration> Configurations = new List<Configuration>\n        {\n            // Assume other configurations are added here...\n            new Configuration { Name = "myConfig", Value = "expectedValue" }\n        };\n\n        Configuration config = Configurations.Find(delegate(Configuration c)\n        {\n            return c.Name == "myConfig";\n        });\n\n        if (config != null)\n        {\n            Console.WriteLine("Found: " + config.Value);\n        }\n        else\n        {\n            Console.WriteLine("Not found.");\n        }\n    }\n}