using System;\nusing System.Windows.Forms;\n\nnamespace CFMemTest\n{\n    public partial class Form1 : Form\n    {\n        private Panel _currentPanel;\n\n        public Form1()\n        {\n            InitializeComponent();\n        }\n\n        // Calling this event handler multiple times causes the memory leak if not disposed properly\n        private void Button1_Click(object sender, EventArgs e)\n        {\n            // Dispose of any previously created panel to clean up resources\n            if (_currentPanel != null)\n            {\n                _currentPanel.Dispose();\n            }\n\n            _currentPanel = new Panel();\n            \n            // Add the panel to a container or it won't show\n            this.Controls.Add(_currentPanel);\n        }\n    }\n}