protected void FileUploadControl_UploadedComplete(object sender, EventArgs e)\n{\n    if (FileUpload1.HasFile)\n    {\n        string filename = Path.GetFileName(FileUpload1.FileName);\n        string ext = Path.GetExtension(filename).ToLower();\n\n        // Define the allowed extensions\n        List<string> validExtensions = new List<string>() { ".gif", ".jpg", ".jpeg", ".png", ".bmp" };\n\n        if (validExtensions.Contains(ext))\n        {\n            // File extension is valid, proceed with saving the file\n            FileUpload1.SaveAs(Server.MapPath("~/uploads/" + filename));\n        }\n        else\n        {\n            // Invalid file type\n            lblMessage.ForeColor = System.Drawing.Color.Red;\n            lblMessage.Text = "Invalid file format. Only .gif, .jpg, .jpeg, .png, and .bmp files are allowed.";\n        }\n    }\n}