using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        PerformanceCounterBase p = new ServicePerformanceCounters();\n        string categoryName = "ServiceModelEndpoint 4.0.0.0";\n        string instanceName = "<YourInstance>";\n\n        PerformanceCounter callsSentCounter = new PerformanceCounter(categoryName, "Calls Sent", instanceName);\n        PerformanceCounter callsReceivedCounter = new PerformanceCounter(categoryName, "Calls Received", instanceName);\n\n        Console.WriteLine("Calls Sent: {0}", callsSentCounter.NextValue());\n        Console.WriteLine("Calls Received: {0}", callsReceivedCounter.NextValue());\n\n        // Dispose of the counters when done.\n        callsSentCounter.Close();\n        callsReceivedCounter.Close();\n    }\n}