using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "http://example.com"; // Replace with your URL\n        using (WebClient client = new WebClient())\n        {\n            try\n            {\n                byte[] data = client.DownloadData(url);\n                string contentType = client.ResponseHeaders["Content-Type"];\n                \n                if (!string.IsNullOrEmpty(contentType) && contentType.StartsWith("text/html", StringComparison.OrdinalIgnoreCase))\n                {\n                    // Process as HTML\n                    Console.WriteLine("Downloaded HTML:");\n                    Console.WriteLine(System.Text.Encoding.UTF8.GetString(data));\n                }\n                else\n                {\n                    Console.WriteLine("The content is not an HTML file.");\n                }\n            }\n            catch (WebException ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n        }\n    }\n}