using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Xml;\n\npublic class KeyStorage\n{\n    public static void SaveKeyAndIV(string filePath, byte[] key, byte[] iv)\n    {\n        // Convert the key and IV to Base64 strings\n        string base64Key = Convert.ToBase64String(key);\n        string base64Iv = Convert.ToBase64String(iv);\n\n        // Create an XML document and store the Base64 encoded key and IV\n        XmlDocument xmlDoc = new XmlDocument();\n        XmlElement root = xmlDoc.CreateElement("EncryptionParameters");\n        xmlDoc.AppendChild(root);\n\n        XmlElement xmlKey = xmlDoc.CreateElement("Key");\n        xmlKey.InnerText = base64Key;\n        root.AppendChild(xmlKey);\n\n        XmlElement xmlIv = xmlDoc.CreateElement("IV");\n        xmlIv.InnerText = base64Iv;\n        root.AppendChild(xmlIv);\n\n        // Save the XML document to a file\n        xmlDoc.Save(filePath);\n    }\n\n    public static void LoadKeyAndIV(string filePath, out byte[] key, out byte[] iv)\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load(filePath);\n\n        string base64Key = xmlDoc.SelectSingleNode("EncryptionParameters/Key").InnerText;\n        string base64Iv = xmlDoc.SelectSingleNode("EncryptionParameters/IV").InnerText;\n\n        // Convert Base64 strings back to byte arrays\n        key = Convert.FromBase64String(base64Key);\n        iv = Convert.FromBase64String(base64Iv);\n    }\n}