using OpenQA.Selenium;\n   using OpenQA.Selenium.Chrome;\n   using System;\n\n   class Program\n   {\n       static void Main()\n       {\n           var options = new ChromeOptions();\n           options.AddArgument("--headless"); // Run in headless mode\n\n           IWebDriver driver = new ChromeDriver(options);\n           IJavaScriptExecutor js = (IJavaScriptExecutor)driver;\n\n           try\n           {\n               driver.Navigate().GoToUrl("http://services.cftc.gov/sirt/sirt.aspx?Topic=ForeignPart30Exemptions");\n               var showAllCheckbox = driver.FindElement(By.Id("ctl00_ContentPlaceHolder1_GenericWebUserControl_ShowAllCheckBox"));\n               showAllCheckbox.Click();\n\n               // Wait for the page to update after clicking\n               // You can use WebDriverWait or Thread.Sleep\n               \n               string htmlSource = driver.PageSource;\n               Console.WriteLine(htmlSource);\n           }\n           finally\n           {\n               driver.Quit();\n           }\n       }\n   }